import { useState } from "react";
import { Container } from "./styles";
import githubIcon from "../../assets/github.svg";
import externalLink from "../../assets/external-link.svg";
import ScrollAnimation from "react-animate-on-scroll";

export function Project() {
  const [expandedIndex, setExpandedIndex] = useState<number | null>(null);

  const toggleDescription = (index: number) => {
    setExpandedIndex(expandedIndex === index ? null : index);
  };

  const projects = [
    {
      title: "E-Shop - Dynamic E-Commerce with Real-Time Seller-User Interaction",
      description: `E-Shop is a cutting-edge e-commerce platform built with React,
      Tailwind CSS, and Material UI. It features a dynamic real-time
      messaging system powered by Socket.IO, enabling seamless
      communication between users and sellers. With integrated Stripe
      payments, cash on delivery options, and comprehensive dashboards
      for sellers and admins, users can easily manage products,
      orders, and events. This platform delivers a modern shopping
      experience with product ratings and full administrative control,
      enhancing convenience for both customers and merchants alike.`,
      tech: ["React", "Node.js", "Tailwind CSS", "Material UI", "Socket.IO", "MongoDb"],
      link: "https://e-shop-xf6x.vercel.app/",
    },
    {
      title: "Sayes Cell - Your Trusted Mobile and Accessories Store",
      description: `Sayes Cell is an online store offering the latest phones and
      accessories. The site features a user-friendly shopping
      experience, showcasing various products such as perfumes, games,
      tablets, and mobile phones. It also provides phone maintenance
      services like screen repairs and battery replacements.`,
      tech: ["Laravel", "Bootstrap", "MySQL"],
      link: null,
    },
    // Add more projects here
  ];

  return (
    <Container id="project">
      <h2>My Projects</h2>
      <div className="projects">
        {projects.map((project, index) => (
          <ScrollAnimation animateIn="flipInX" key={index}>
            <div className="project">
              <header>
                <svg
                  width="50"
                  xmlns="http://www.w3.org/2000/svg"
                  role="img"
                  viewBox="0 0 24 24"
                  fill="none"
                  stroke="#23ce6b"
                  strokeWidth="1"
                  strokeLinecap="round"
                  strokeLinejoin="round"
                >
                  <title>Folder</title>
                  <path d="M22 19a2 2 0 0 1-2 2H4a2 2 0 0 1-2-2V5a2 2 0 0 1 2-2h5l2 3h9a2 2 0 0 1 2 2z"></path>
                </svg>
                <div className="project-links">
                  {project.link && (
                    <a href={project.link} target="_blank" rel="noreferrer">
                      <img src={externalLink} alt="Visit site" />
                    </a>
                  )}
                </div>
              </header>
              <div className="body">
                <h3>{project.title}</h3>
                <p>
                  {expandedIndex === index
                    ? project.description
                    : `${project.description.substring(0, 100)}... `}
                  {project.description.length > 100 && (
                    <span
                      onClick={() => toggleDescription(index)}
                      style={{ color: "#23ce6b", cursor: "pointer" }}
                    >
                      {expandedIndex === index ? "Show Less" : "Read More"}
                    </span>
                  )}
                </p>
              </div>
              <footer>
                <ul className="tech-list">
                  {project.tech.map((tech, i) => (
                    <li key={i}>{tech}</li>
                  ))}
                </ul>
              </footer>
            </div>
          </ScrollAnimation>
        ))}
      </div>
    </Container>
  );
}
